mapper.select:
SELECT buyNum, fishName, buyerId, registDate, uploadDate, deadline, weight, saleStatus, price, successSellerId, buyListComment, location FROM buyList
WHERE buyerId = #{search}
and saleStatus !='onSale' and saleStatus !='refundComplete'

mapper.update:
 
  	 	UPDATE buylist
     	SET saleStatus = 'saleComplete'
    	WHERE buyNum = #{buyNum}
